import React, { Component } from 'react';

class Item extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicked: false,
        };
    }

    handleClick = () => {
        this.setState({ clicked: true });
        this.props.checkIfLost();
    };

    render() {
        return (
            <div 
                className="item"
                onClick={this.handleClick}
            >
                {this.props.value}
            </div>
        );
    }
}

export default Item;
